import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import "./../scss/ClickEvents.scss"
const rand = () => Math.floor(Math.random() * 255);

const genData = () => ({
  labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
  datasets: [
    {
      type: 'line',
      label: 'Dataset 1',
      borderColor: 'rgb(231, 27, 27)',
      borderWidth: 2,
      fill: false,
      data: [rand(), rand(), rand(), rand(), rand(), rand()],
    },
    {
      type: 'bar',
      label: 'Dataset 2',
      backgroundColor: `rgb(11, 228, 22)`,
      data: [rand(), rand(), rand(), rand(), rand(), rand(), rand()],
      borderColor: 'white',
      borderWidth: 2,
    },
    {
      type: 'bar',
      label: 'Dataset 3',
      backgroundColor: `#577d4b`,
      data: [rand(), rand(), rand(), rand(), rand(), rand(), rand()],
    },
  ],
});

const options = {
  scales: {
    yAxes: [
      {
        ticks: {
          beginAtZero: true,
        },
      },
    ],
  },
};

const data = genData();

const ClickEvents = () => {
  const [clickedDataset, setClickedDataset] = useState('');
  const [clickedElement, setClickedElement] = useState('');
  const [clickedElements, setClickedElements] = useState('');

  const getDatasetAtEvent = dataset => {
    if (!dataset.length) return;

    const datasetIndex = dataset[0].datasetIndex;
    setClickedDataset(data.datasets[datasetIndex].label);
  };

  const getElementAtEvent = element => {
    if (!element.length) return;

    const { datasetIndex, index } = element[0];
    setClickedElement(
      `${data.labels[index]} - ${data.datasets[datasetIndex].data[index]}`
    );
  };

  const getElementsAtEvent = elements => {
    if (!elements.length) return;

    setClickedElements(elements.length);
  };

  return (
    <>
     




      <div className="graph_info">
          <div className="box">

          <div className="chart_main">
      <Bar
        data={data}
        options={options}
        getDatasetAtEvent={getDatasetAtEvent}
        getElementAtEvent={getElementAtEvent}
        getElementsAtEvent={getElementsAtEvent}
      />
      <div className='text-center'>
        <p>{clickedElement}</p>
        <p>{clickedDataset}</p>
        <p>{clickedElements}</p>
      </div>
      </div>


            
          </div>

          <div className="graph_footer">
            <div className="box_legend">
              <div className="dot_legend dot_yellow"></div>
              <div className="graph_legend graph_yellow">
                <p>1O completement guerris</p>
              </div>
            </div>

            <div className="box_legend">
              <div className="dot_legend dot_green"></div>
              <div className="graph_legend graph_green">
                <p>1O completement guerris</p>
              </div>
            </div>

            <div className="box_legend">
              <div className="dot_legend dot_blue"></div>
              <div className="graph_legend graph_blue">
                <p>1O completement guerris</p>
              </div>
            </div>

            <div className="box_legend">
              <div className="dot_legend dot_gris"></div>
              <div className="graph_legend graph_gris">
                <p>1O completement guerris</p>
              </div>
            </div>
          </div>
        </div>

    </>
  );
};

export default ClickEvents;
